CREATE OR REPLACE VIEW "public"."view_movie" AS (
    SELECT
        m.id,
        m.title,
        m.adult,
        m.budget,
        m.homepage,
        m.imdb_id,
        m.original_language,
        m.original_title,
        m.overview,
        m.popularity,
