        -- Aggregate genres
        ( SELECT JSON_AGG(g) FROM public.tbl_genre AS g
            JOIN public.mtm_movie_genre AS mg ON mg.genreid = g.id
            WHERE mg.movieid = m.id
        ) AS genres,


